mapper.select:
select * from tb_member where 1=1 


	 and idx=#{idx} 



	 and id=#{id} 



	 and password=#{password}

mapper.update:

UPDATE tb_member
SET useYN='N', updatedate=now()
WHERE id=#{id};

mapper.select:
select * from tb_member where useYN='Y' 


	 and id=#{id} 



	 and password=#{password}
