mapper.select:
select * from tb_user where 1=1 


	 and idx=#{idx} 



	 and id=#{id} 



	 and name=#{name} 



	 and password=#{password}

mapper.update:

UPDATE tb_user
SET id=#{id}, password=#{password}, position=#{position}, name=#{name}, phone=#{phone}, email=#{email}, updatedate=now()
WHERE idx=#{idx};
